def eth_price():
    while True:
        try:
            r = requests.get('https://min-api.cryptocompare.com/data/price?fsym=ETH&tsyms=USD')
            return r.json()['USD']
        except Exception as e:
            logger.error(f'ERROR | {e}')
            time.sleep(15)


async def stake(key):
    ADDRESS = web3.eth.account.from_key(key).address
    contract = web3.eth.contract(abi=json.loads(
        '[{"inputs":[],"stateMutability":"nonpayable","type":"constructor"},{"anonymous":false,"inputs":[{"indexed":true,"internalType":"address","name":"user","type":"address"},{"indexed":true,"internalType":"contract Authority","name":"newAuthority","type":"address"}],"name":"AuthorityUpdated","type":"event"},{"anonymous":false,"inputs":[{"indexed":true,"internalType":"address","name":"user","type":"address"},{"indexed":true,"internalType":"address","name":"newOwner","type":"address"}],"name":"OwnerUpdated","type":"event"},{"anonymous":false,"inputs":[{"indexed":false,"internalType":"address","name":"user","type":"address"},{"indexed":false,"internalType":"address","name":"token","type":"address"},{"indexed":false,"internalType":"address","name":"depositToken","type":"address"},{"indexed":false,"internalType":"address","name":"swapTarget","type":"address"},{"indexed":false,"internalType":"address","name":"vault","type":"address"},{"indexed":false,"internalType":"uint256","name":"amount","type":"uint256"},{"indexed":false,"internalType":"uint256","name":"depositAmount","type":"uint256"}],"name":"Zap","type":"event"},{"inputs":[],"name":"ETH","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"authority","outputs":[{"internalType":"contract Authority","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"owner","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"contract Authority","name":"newAuthority","type":"address"}],"name":"setAuthority","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"newOwner","type":"address"}],"name":"setOwner","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"user","type":"address"},{"internalType":"address","name":"token","type":"address"},{"internalType":"address","name":"depositToken","type":"address"},{"internalType":"address","name":"swapTarget","type":"address"},{"internalType":"address","name":"vault","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"},{"internalType":"bytes","name":"swapData","type":"bytes"}],"name":"swapAndDeposit","outputs":[],"stateMutability":"payable","type":"function"},{"inputs":[{"internalType":"address","name":"target","type":"address"}],"name":"toggleApprove","outputs":[],"stateMutability":"nonpayable","type":"function"}]'))
    rep = 0
    while True:
        try:
            value = web3.to_wei(round(random.uniform(51.2,51.8)/eth_price(),random.randint(3,5)),'ether')
            data_swap =  req_stake(value).strip('0x')
            tx = {
                'nonce': await web3.eth.get_transaction_count(ADDRESS),
                'gasPrice': await web3.eth.gas_price,  # web3.to_wei(0.1,'gwei'),  # web3.eth.gas_price,
                'chainId': await web3.eth.chain_id,
                'from': ADDRESS,
                'to': web3.toChecksumAddress('0xef5f0c5d39ad78b5fd3bdb0e55704c399ee4f04a'),
                'gas':4000000,
                'data': contract.encodeABI('swapAndDeposit', args=[ADDRESS,
                                                                   web3.toChecksumAddress('0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE'),
                                                                   web3.toChecksumAddress('0x8c6f28f2F1A3C87F0f938b96d27520d9751ec8d9'),
                                                                   web3.toChecksumAddress('0x1111111254760F7ab3F16433eea9304126DCd199'),
                                                                   web3.toChecksumAddress('0xB7b4270cFD938F4F1C111ac819e7365E8Ce0300a'),
                                                                   value,web3.toBytes(hexstr=data_swap)]),
                'value':value
            }

            await web3.eth.call(tx)

            sign = web3.eth.account.sign_transaction(tx, key)
            hash = await web3.eth.send_raw_transaction(sign.rawTransaction)
            # logger.info(f'swapAndDeposit | {ADDRESS} {hash.hex()}')
            return hash
        except Exception as e:
            logger.error(f'ERROR | {ADDRESS} | {e} | TRY AGAIN')
            rep+= 1
            if rep >4:
                logger.error(f'ERROR | {ADDRESS} | FAILED STAKE')
                return

            await asyncio.sleep(15)


def req_stake(amount):
    params = {
        'fromTokenAddress': '0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE',
        'toTokenAddress': '0x8c6f28f2F1A3C87F0f938b96d27520d9751ec8d9',
        'amount': str(int(amount)),
        'fromAddress': '0xef5f0c5d39ad78b5fd3bdb0e55704c399ee4f04a',
        'slippage': '5',
        'disableEstimate': 'true',
    }
    while True:
        try:
            response = requests.get('https://api.1inch.io/v4.0/10/swap', params=params)
            return (response.json())['tx']['data']
        except Exception as e:
            logger.error(f'{e}')
            time.sleep(15)

async def work_polynomial(key):
    ADDRESS = web3.eth.account.from_key(key).address

    tm = random.randint(3, 15)

    await asyncio.sleep(tm)
    if await web3.eth.get_balance(ADDRESS) >=web3.to_wei(57/eth_price(),'ether'):
        tx = await stake(key)
        if await verif_tx(tx):
            return True
        else:
            return False
    return True

