async def work_pooltog(key):
    ADDRESS = web3.eth.account.from_key(key).address

    tm = random.randint(3, 15)

    await asyncio.sleep(tm)
    if not (await balance_token(ADDRESS,'0x62bb4fc73094c83b5e952c2180b23fa7054954c4')):
        if await check_approve(key, '0x79Bc8bD53244bC8a9C8c27509a2d573650A83373',
                               '0x7f5c764cbc14f9669b88837ca1490cca17c31607'):

            gas = await approve_gas(key, '0x79Bc8bD53244bC8a9C8c27509a2d573650A83373',
                                    '0x7f5c764cbc14f9669b88837ca1490cca17c31607')
            await verif_tx(gas)
            await asyncio.sleep(random.randint(10, 15))

        if await balance_token(ADDRESS,'0x62bb4fc73094c83b5e952c2180b23fa7054954c4') == 0:
            tx= await stake(key)
            if await verif_tx(tx):
                return True
            else:
                return False
        return False
    else:
        return True




