async def work_uniswap(key):
    ADDRESS = web3.eth.account.from_key(key).address

    tm = random.randint(3, 15)
    await asyncio.sleep(tm)

    if await check_approve(key, '0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45',
                           '0x4200000000000000000000000000000000000006'):
        gas = await approve_gas(key, '0x68b3465833fb72A70ecDF485E0e4C7bD8665Fc45',
                                '0x4200000000000000000000000000000000000006')
        await verif_tx(gas)
        await asyncio.sleep(random.randint(10, 15))

    tx = await swap_to(key)
    if await verif_tx(tx):
        return True
    else:
        return False

    return False