async def approv(key, SPEND, TOKEN):
    if await check_approve(key, SPEND,
                           TOKEN):
        gas = await approve_gas(key, SPEND,
                                TOKEN)
        await verif_tx(gas)
        await asyncio.sleep(random.randint(10, 15))


async def work_velodrome(key):
    ADDRESS = web3.eth.account.from_key(key).address

    tm = random.randint(3, 15)

    await asyncio.sleep(tm)

    await approv(key, '0x9c12939390052919aF3155f41Bf4160Fd3666A6f', '0x7f5c764cbc14f9669b88837ca1490cca17c31607')
    await approv(key, '0x9c12939390052919aF3155f41Bf4160Fd3666A6f', '0x7f5c764cbc14f9669b88837ca1490cca17c31607')

    if await balance_token(ADDRESS, '0x4200000000000000000000000000000000000006') <= 0.016 * 10 ** 18 and \
            await balance_token(ADDRESS, '0x79c912fef520be002c2b6e57ec4324e260f38e50') == 0 \
            and await balance_token(ADDRESS, '0xe2cec8ab811b648ba7b1691ce08d5e800dd0a60a') == 0 :
        tx = await swap(key)
        await verif_tx(tx)
        await asyncio.sleep(10, 15)

    await approv(key, '0x9c12939390052919aF3155f41Bf4160Fd3666A6f', '0x4200000000000000000000000000000000000006')
    await approv(key, '0xE2CEc8aB811B648bA7B1691Ce08d5E800Dd0a60a', '0x79c912fef520be002c2b6e57ec4324e260f38e50')

    if await balance_token(ADDRESS, '0x79c912fef520be002c2b6e57ec4324e260f38e50') == 0 \
            and await balance_token(ADDRESS, '0xe2cec8ab811b648ba7b1691ce08d5e800dd0a60a') == 0:
        tx = await addLiquidity(key)
        await verif_tx(tx)
        await asyncio.sleep(10, 15)

    if await balance_token(ADDRESS, '0x79c912fef520be002c2b6e57ec4324e260f38e50') != 0:
        tx = await deposit(key)
        await verif_tx(tx)
        await asyncio.sleep(10, 15)

    if await balance_token(ADDRESS, '0xe2cec8ab811b648ba7b1691ce08d5e800dd0a60a') != 0:
        tx = await Withdraw(key)
        await verif_tx(tx)
        await asyncio.sleep(10, 15)

    await approv(key, '0x9c12939390052919aF3155f41Bf4160Fd3666A6f', '0x79c912fef520be002c2b6e57ec4324e260f38e50')

    if await balance_token(ADDRESS, '0x79c912fef520be002c2b6e57ec4324e260f38e50') != 0:
        tx = await removeLiquidity(key)
        if await verif_tx(tx):
            # logger.success('Complete | {}'.format(ADDRESS))
            return True
        else:
            # logger.error('VELODROME | Complete | {}'.format(ADDRESS))
            return False

    return False